package Task;

public class VariablesTest {
 static int a=10,b=50;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		add();
		sub();
		mul();
	}
	static void add() {
	  int c=a+b;
		System.out.println(c);
	}
	static void sub() {
		int k=a-b;
		System.out.println(k);
	}
	static void mul() {
		int z=a*b;
		System.out.println(z);
	}
}
	
		
		
		




